(()=>{"use strict";((e,t=!1)=>{const n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),s=document.getElementById("timer-seconds"),m=new Date(e).getTime();let r;const c=e=>e<10?"0"+e:""+e;r=setInterval((()=>{const e=(()=>{const e=(new Date).getTime(),n=(m-e)/1e3,o=Math.floor(n/3600/24);let s=Math.floor(n/3600);s=t?s%24:s;const r=Math.floor(n/60)%60,i=Math.floor(n)%60;return{timeRemaining:n,days:c(o),hours:c(s),minutes:c(r),seconds:c(i)}})();e.timeRemaining<=0?clearInterval(r):(n.textContent=e.hours,o.textContent=e.minutes,s.textContent=e.seconds)}),1e3)})("08 march 2022"),console.log("Запущен второй модуль")})();